'                       when ''javascript-function-body'' then',
'                           l_js_message_code',
'                       end',
'                  );',
'            end if; ',
'        end if;',
'    end if;',
'',
'    apex_json.close_object;',
'    ',
'    l_result.javascript_function := ''function(){FOS.utils.notification(this, '' || apex_json.get_clob_output || '', ''|| l_init_js_fn || '');}'';',
'    ',
'    apex_json.free_output;',
'',
'    return l_result;',
'end render;'))
